fn generate_skewed_sawtooth_table(skew_factor: f32) -> [f32; TABLE_SIZE] {
    let mut table = [0.0; TABLE_SIZE];
    let clamped_skew = skew_factor.clamp(0.0, 1.0);
    for i in 0..TABLE_SIZE {
        let phase = i as f32 / (TABLE_SIZE - 1) as f32;
        // Use an exponential function to skew the phase
        let skewed_phase = phase.powf(clamped_skew + 0.1); // Add a small offset to avoid powf(0, <1) issues
        table[i] = 2.0 * skewed_phase - 1.0;
    }
    table
}

// Generated with 0.4,0.5
fn generate_bent_sawtooth_table(bend_point: f32, bend_amount: f32) -> [f32; TABLE_SIZE] {
    let mut table = [0.0; TABLE_SIZE];
    let clamped_bend_point = bend_point.clamp(0.0, 1.0);
    let normalized_bend_amount = bend_amount.clamp(-1.0, 1.0);

    for i in 0..TABLE_SIZE {
        let phase = i as f32 / (TABLE_SIZE - 1) as f32;
        if phase < clamped_bend_point {
            table[i] = -1.0 + (2.0 + normalized_bend_amount) * phase / clamped_bend_point;
        } else {
            table[i] = -1.0 + (2.0 - normalized_bend_amount) * (phase - clamped_bend_point) / (1.0 - clamped_bend_point) + (2.0 + normalized_bend_amount);
            table[i] -= 2.0; // Normalize to -1 to 1
        }
    }
    table
}

